import CircleLoader from "../App/CircleLoader/CircleLoader";
import {Button} from "antd";
import { actionsType } from "../../Shopping/shopping";
import { useDispatch  } from "react-redux";
import { useParams } from "react-router-dom";
import {
    StyledText,
    MainWrapper,
    StyledName,
    StyledPrice,
    StyledT,
    StyledB,
    StyledC,
    StyledButton
} from "./SelectedItem.styled";

import { getAllTables, getTablesById } from "../../server";
import { useEffect } from "react";
import { useLocation } from "react-router-dom";
import { useState } from "react"
import Card from "antd/es/card/Card";



const SelectedItem = () => {
    const [tables,setTables] = useState([]);
    const dispatch = useDispatch()
    const [isLoading, setIsLoading] = useState(false);
    const location = useLocation();
    const [quantity, setQuantity] = useState(1);
    const [isVisible,setIsVisible] = useState(false)


    useEffect(()=>{
        setIsLoading(true);
        const tableId = parseInt(id);
        console.log (tableId)
        getTablesById(tableId).then((tables)=>{
            setTables(tables)
            console.log (tableId)
            setIsLoading(false);
            console.log (tableId)
        }
        )
    },[])


    const { id } = useParams();
    const tableId = parseInt(id);
    const tableData = tables.find((t) => t.id === Number(tableId));


    if (!tableData) {
        return <div>Loading...</div>;
    }

    function addItemToCart() {
        dispatch({
            type: actionsType.addToCart,
            tableToAdd: { ...tableData, quantity: parseInt(quantity, 10) || 1 },
        });
        setIsVisible(true) };

        const handleQuantityChange = (e) => {
            const inputValue = parseInt(e.target.value, 10) || 0;
            setQuantity(Math.max(inputValue, 0));
        };

    return(
        <div>
            <MainWrapper>
            {isLoading ? <CircleLoader/> : ''}
            <Card
            hoverable
            cover = {<img style={{marginLeft:100,width:500,height:500}} src={tableData.image}/>}
            >
                <h2 style={{textAlign: "center"}}>{tableData.title}</h2>
            </Card>
            <div>
                <StyledText>
                    <p>{tableData.text}</p>
                </StyledText>
            </div>
            <div>
                <StyledC>
                    <p>Size: {tableData.size}</p>
                    <p>Price: {tableData.color}</p>
                </StyledC>
                <input
                type="number"
                value={quantity}
                onChange={handleQuantityChange}
                ></input>
                <StyledButton onClick={addItemToCart}>
                    Add to Cart
                </StyledButton>
            </div>
            {isVisible &&
                <div className="message">
                    <p>Item has been addwd to cart</p>
                    <a href="/cart">Click here to go to cart</a>
                </div>}
            </MainWrapper>
        </div>
    )
}

export default SelectedItem;
