import React from "react";
import CardItem from "../../components/CardItem/CardItem";
import { useState, useEffect } from "react";
import { getAllTables } from "../../server";
import { CardWrapper } from "../Home/Home.styled";

const MainCatalog = ({filters, tablesList})=>{
    const [tables,setTables] = useState([]);
    const [isLoading, setIsLoading] = useState(false);

    const [filteredTables, setFilteredTables] = useState(tablesList);

    useEffect(() => {
        const filteredItems = tablesList.filter((item) => {

            return (
                // (!searchInput || title.toLowerCase().includes(searchInput.toLowerCase())) &&
                (filters.color === "all" || item.color === filters.color) &&
                ((filters.price === "all") ||
                    (filters.price === "cheap" && item.price < 100) ||
                    (filters.price === "average" && item.price >= 100 && item.price < 500)||
                    (filters.price === "expensive" && item.price >= 500)) &&

                (filters.size === "all" || item.size === filters.size)

            );
        });
        setFilteredTables(filteredItems);

    },  [filters]);
    useEffect(() => {
        console.log('Updated Filters:', filters);

      }, [filters]); // Trigger this effect whenever filters change

    useEffect(()=>{
        setIsLoading(true);
        getAllTables().then((tables)=>{
            setTables(tables)
            setIsLoading(false);
        }
        )
    },[])

        return(
        <CardWrapper>
        {filteredTables.map((table)=>(
            <CardItem 
                key={table.id}
                title={table.title}
                text={table.text}
                image = {table.image}
                price={table.price}
                size={table.size}
                color={table.color}
                id={table.id}
            />
        ))}
        </CardWrapper>
        )

}

export default MainCatalog
