import {React} from 'react';
import Select from "./Select";
import Search from "./Search";
import "./Filter.styled";
import { StyledFilterBlock } from './Filter.styled';

const CatalogFilters = ({filters, handleApplyFilter }) => {
    
    const handleFilterChange = (event) => {
      const { name, value } = event.target;
      handleApplyFilter((prevFilters) => ({ ...prevFilters, [name]: value }));
    };
  
    
    const colorOptions = [
      { label: "All", value: "all" },
      { label: "White", value: "white" },
      { label: "Black", value: "black" },
      { label: "Brown", value: "brown" },
    ];
  
    const priceOptions = [
      { label: "All", value: "all" },
      { label: "Cheap", value: "cheap" },
      { label: "Average", value: "average" },
      { label: "Expensive", value: "expensive" },
    ];
  
    const sizeOptions = [
      { label: "All", value: "all" },
      { label: "Small", value: "small" },
      { label: "Medium", value: "medium" },
      { label: "Large", value: "large" },
    ];
  
    return (
      <StyledBlock>
        <catalogFilters style={{marginLeft: 80, justifyContent: 'space-around'}}>
          <Search searchValue={filters.title} handleSearchValueChange={onFilterValueChanged}/>
          <label htmlFor="color-filter" style={{marginLeft: 80, justifyContent: 'space-around', fontSize: 18}}>
            Color:
            <Select
              id="color-filter"
              name="color"
              onChange={onFilterValueChanged}
              options={colorOptions}
            />
          </label>
    
          <label htmlFor="price-filter" style={{marginLeft: 80, justifyContent: 'space-around', fontSize: 18}}>
            Price:
            <Select
              id="price-filter"
              name="price"
              onChange={onFilterValueChanged}
              options={priceOptions}
            />
          </label>
    
          <label htmlFor="size-filter" style={{marginLeft: 80, justifyContent: 'space-around', fontSize: 18}}>
            Size:
            <Select
              id="size-filter"
              name="size"
              onChange={onFilterValueChanged}
              options={sizeOptions}
            />
          </label>
        </catalogFilters>
      </StyledBlock>
    );
  };
  
  export default CatalogFilters;
