import CircleLoader from "../App/CircleLoader/CircleLoader";
import {Button} from "antd";
import { actionsType } from "../../Shopping/shopping";
import { useDispatch  } from "react-redux";
import { useParams } from "react-router-dom";
import {
    StyledText,
    MainWrapper,
    StyledName,
    StyledPrice,
    StyledT,
    StyledB,
    StyledC,
    StyledButton
} from "./SelectedItem.styled";

import { getAllTables, getTablesById } from "../../server";
import { useEffect } from "react";
import { useLocation } from "react-router-dom";
import { useState } from "react"
import Card from "antd/es/card/Card";



const SelectedItem = () => {
    const [tables,setTables] = useState([]);
    const dispatch = useDispatch()
    const [isLoading, setIsLoading] = useState(false);
    const location = useLocation();
    const [quantity, setQuantity] = useState(1);
    const [isVisible,setIsVisible] = useState(false)


    useEffect(()=>{
        setIsLoading(true);
        console.log (tableId)
        const tableId = parseInt(id);
        console.log (tableId)
        getTablesById(tableId).then((tables)=>{
            setTables(tables)
            console.log (tableId)
            setIsLoading(false);
            console.log (tableId)
        }
        )
    },[])


    const { id } = useParams();
    const tableId = parseInt(id);
    const tableData = tables.find((t) => t.id === Number(tableId));

    if (!tableData) {
        return <div>Loading...</div>;
    }

    function addItemToCart() {
        dispatch({
            type: actionsType.addToCart,
            tableToAdd: { ...tableData, quantity: parseInt(quantity, 10) || 1 },
        });
        setIsVisible
    }

    return(
        <div>
            <MainWrapper>
            {isLoading ? <CircleLoader/> : ''}
            <img style={{marginLeft:100,width:500,height:500}} src={tableData.image}/>
                <StyledT>
                    <StyledName>
                        <p>{tableData.title}</p>
                    </StyledName>
                    <StyledText>
                        <p>{tableData.text}</p>
                    </StyledText>
                    <StyledC>
                        <p>Size: {tableData.size}</p>
                        <p>Price: {tableData.color}</p>
                    </StyledC>
                    <StyledB>
                        <StyledPrice>
                            <p>${tableData.price}</p>
                        </StyledPrice>
                        <StyledButton onClick={addItemToCart}>
                            Add to Cart
                        </StyledButton>
                        {/* <Button className="add_to_cart" onClick={addItemToCart}>Add to cart</Button> */}
                    </StyledB>
                </StyledT>
            </MainWrapper>
        </div>
    )
}

export default SelectedItem;
