import axios from "axios";

const BASE_URL ="http://localhost:8080/tables";
const FILTER_URL = "http://localhost:8080/tables/filter";

const createUrl = ({title, price, size, color}) => {
    let url = '';
    url += price != ''?`?price=${price}`:'?';
    url += title != ''?`&title=${title}`:'';
    url += size != ''?`&size=${size}`:'';
    url += color != ''?`&color=${color}`:'';
    return url;
}

export const getAllTables = async ()=>{
    return axios.get(BASE_URL).then(
        response => response.data
    )

}

export const getFilteredTables = async ({size, color, price, title}) =>{
    const url = createUrl({size, color, price, title});
    const response = await axios.get(FILTER_URL + url);

    return response.data;
}
