// import Table1 from "../../../Icons/56121403SD00205_A2_803x602.jpg"
import Table2 from "../../../src/Icons/56121403SD00233_A2_803x602.jpg"
import Table3 from "../../../src/Icons/56121403SD00269_A2_803x602.jpg"
import Table4 from "../../../src/Icons/56121403SD00200_A2_803x602.jpg"
import Table1 from "../../../src/Icons/56121403SD00205_A2_803x602.jpg"

import {
    StyledButton,
    StyledText,
    MainWrapper,
    StyledName,
    StyledPrice,
    StyledT,
    StyledB,
    StyledC
} from "./SelectedItem.styled";

// import {getTables} from "../App/Catalog/Catalog";
import { getAllTables } from "../../server";
import { useEffect } from "react";
import { useLocation } from "react-router-dom";
import { useState } from "react"

const tables =[
    {
        id: 1,
        title: "Dining table",
        text: "Welcome to our shop of chairs and tables! We present to your attention an elegant and functional dining table, ideal for four people. Its modern design and high quality materials make it an excellent choice for your dining room or kitchen. This table is combined with any interior and will create an atmosphere of coziness and comfort during family lunches or dinners. Choose our dining table and enjoy the style and convenience of every meal!",
        image: Table1,
        price: 40,
        size:"large",
            color:"white"
    },
    {
        id: 2,
        title: "Desktop",
        text: "Welcome to the world of innovation and productivity! Our desk for programmers is specially designed for those who understand the importance of a comfortable workspace. With adjustable height, a large work space and built-in organizers, this desk not only supports work efficiency, but also allows you to enjoy every working moment. Integrated ports for charging and cable management make it an ideal solution for organizing a modern work environment.",
        image: Table2,
        price: 234,
            size:"small",
            color:"black"
    },
    {
        id: 3,
        title: "Coffee table",
        text: "Welcome to our store, where the perfect atmospheric moment is created for you - with our coffee table! This table impresses with its elegance and practicality, perfect for your living room or modern study. Made of high-quality materials, it not only gives your space charm, but also creates a comfortable place for communication and relaxation. Thanks to its convenient construction and stylish design, this coffee table will be an excellent addition to your interior.",
        image: Table3,
        price: 876,
        size:"medium",
            color:"brown"
    },
    {
        id: 4,
    title: "Office table",
    text: "Welcome to the world of efficient workspace! Our office desk is the perfect combination of style and functionality for your office or home study. Thanks to its minimalist design and excellent quality materials, this desk will provide ergonomics and comfort during the working day. Its spacious work floor, large drawers and modern look make it an ideal choice for a four-person office. Raising your working comfort to a new level - with our office desk!",
    image: Table4,
    price: 865,
    size:"medium",
        color:"black"
    },

];

const SelectedItem = () => {
    const [tables,setTables] = useState([])
    const [isLoading, setIsLoading] = useState(false);
    const location = useLocation();

    const tableId = location.pathname.split("/")[2];

    const tableData = tables.find(t => t.id === Number(tableId ))

    useEffect(()=>{
        setIsLoading(true);
        getAllTables().then((tables)=>{
            setTables(tables)
            setIsLoading(false);
        }
        )
    },[])


    return(
        <div>
            <MainWrapper>
            <img style={{marginLeft:100,width:500,height:500}} src={tableData.image}/>
                <StyledT>
                    <StyledName>
                        <p>{tableData.title}</p>
                    </StyledName>
                    <StyledText>
                        <p>{tableData.text}</p>
                    </StyledText>
                    <StyledC>
                        <p>Size: {tableData.size}</p>
                        <p>Price: {tableData.color}</p>
                    </StyledC>
                    <StyledB>
                        <StyledPrice>
                            <p>${tableData.price}</p>
                        </StyledPrice>
                        <StyledButton>
                            Buy
                        </StyledButton>
                    </StyledB>
                </StyledT>
            </MainWrapper>
        </div>
    )
}

export default SelectedItem;
