import CircleLoader from "../App/CircleLoader/CircleLoader";
import {Button} from "antd";

import {
    StyledButton,
    StyledText,
    MainWrapper,
    StyledName,
    StyledPrice,
    StyledT,
    StyledB,
    StyledC
} from "./SelectedItem.styled";

import { getAllTables, getTablesById } from "../../server";
import { useEffect } from "react";
import { useLocation } from "react-router-dom";
import { useState } from "react"


const SelectedItem = () => {
    const [tables,setTables] = useState([]);

    const [isLoading, setIsLoading] = useState(false);
    const location = useLocation();

    const tableId = location.pathname.split("/")[2];

    useEffect(()=>{
        setIsLoading(true);
        getTablesById(tableId).then((tables)=>{
            setTables(tables)
            setIsLoading(false);
        }
        )
    },[])



    const tableData = tables(t => t.id === Number(tableId ))
    if (!tableData) {
        return <div>Loading...</div>;
    }

    return(
        <div>
            <MainWrapper>
            {isLoading ? <CircleLoader/> : ''}
            <img style={{marginLeft:100,width:500,height:500}} src={tableData.image}/>
                <StyledT>
                    <StyledName>
                        <p>{tableData.title}</p>
                    </StyledName>
                    <StyledText>
                        <p>{tableData.text}</p>
                    </StyledText>
                    <StyledC>
                        <p>Size: {tableData.size}</p>
                        <p>Price: {tableData.color}</p>
                    </StyledC>
                    <StyledB>
                        <StyledPrice>
                            <p>${tableData.price}</p>
                        </StyledPrice>
                        <StyledButton>
                            Buy
                        </StyledButton>
                    </StyledB>
                </StyledT>
            </MainWrapper>
        </div>
    )
}

export default SelectedItem;
